mapper.insert:
INSERT INTO MEMBER
(
	NO
	,NAME
	,ID
	,PWD
	,BIRTH
	,HP
	,EMAIL
	,LOCATION_NO
	,GENDER_CODE
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL
	, #{name}
	, #{id}
	, #{pwd}
	, #{birth}
	, #{hp}
	, #{email}
	, #{locationNo}
	, #{genderCode}
)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE MEMBER
SET
	PWD = #{pwd}
	,HP = #{hp}
	,EMAIL = #{email}
	,LOCATION_NO = #{locationNo}
	,GENDER_CODE = #{genderCode}
WHERE NO = #{no}
